import osfrom random import randintfrom info import level, weapons, hp, bag, weaponsfrom add import items_txt_rmvdef encounter():    chance=randint(1,100)    if chance<46:        weapon=''        chosen_item=''        _run=False        print('Something attacks you!')        input()        os.system('clear')        battle_hp=hp        defense=0        bonus=0        print(enemies)        enemie=enemies[randint(1,len(enemies))-1]        e_lvl=level        e_name=enemie.split('  ')[0]        e_atk=int(int(enemie.split('  ')[1])*(1+level/6))        e_hp=int(enemie.split('  ')[2])*(1+level/5)        e_xp=int(enemie.split('  ')[3])*(level**2)        print(e_name)        print('HP: ' + str(int(e_hp)))        print('ATK: ' + str(e_atk))        print('')        while e_hp>0 and battle_hp>0:            if weapon.lower() =='back' or chosen_item.lower()=='back':            	print('')            print('Actions: Fight--Bag--Run')            weapon=''            chosen_item=''            print('')            action=input()            while action.lower() !='fight' and action.lower() !='run' and action.lower()!='bag' and action.lower()!='0':                action=input()            print('')            if action=='0':                break            elif action.lower()=='fight':                now_weapons = []                for wea in weapons:                    now_weapons.append(wea.split('  ')[0])                print('Weapons: ' + '\n         '.join(now_weapons))                print('')                dam_weapon=''                weapon=input()                if weapon.lower()=='nfls':                	weapon='Newtons Flaming Laser Sword'                while weapon.lower()!='back':                    while dam_weapon=='':                        for con_weapon in weapons:                            if con_weapon.split('  ')[0].lower()==weapon.lower():                                dam_weapon=con_weapon.split('  ')[1]                        if dam_weapon=='':                            weapon=input()                            if weapon.lower()=='nfls':                                weapon='Newtons Flaming Laser Sword'                            if weapon.lower()=='back':                            	break                    if weapon.lower()=='back':                        break                    print('')                    damage= int(dam_weapon)+(level/2*bonus)+randint(0,2)                    e_hp=e_hp - (damage)                    print(e_name +' has lost ' + str(int(damage))+ ' HP!')                    if e_hp<0:                        e_hp=0                    print('')                    break            elif action.lower()=='run':                run=randint(0,1)                if run ==0:                    _run=True                    break                else:                	print('Couldn\'t get away safely!')                	print('')            elif action.lower()=='bag':            	items_name=[]            	for item in bag:            		items_name.append(item.split('  ')[0])            	print('Items: ' + '\n       '.join(items_name))            	dam_item=''            	print('')            	chosen_item=input()            	while chosen_item.lower()!='back':                    while dam_item=='':                        for con_item in bag:                            if chosen_item.lower() in con_item.lower():                                dam_item=con_item                        if dam_item=='':                            chosen_item=input()                             if chosen_item.lower()=='back':                            	break                    if chosen_item.lower()=='back':                        break                     print('')                    item_function=dam_item.split('  ')                    if item_function[1]=='heal':                    	battle_hp+=int(item_function[2])                    	print('The '+item_function[0]+ ' healed you for ' + item_function[2] + ' Hp!')                    elif item_function[1]=='dmg':                    	e_hp-=int(item_function[2])                    	print('The '+item_function[0]+ ' dealt '+item_function[2]+' damage to the '+e_name+'!')                    elif item_function[1]=='boost':                    	if item_function[2]=='defense':                    		defense+=int(item_function[3])                    		print('The '+item_function[0]+' boosted your defense to '+str(defense)+'!')                    	elif item_function[2]=='atk':                    		bonus+=int(item_function[3])                    		print('The '+item_function[0]+' boosted your attack to '+str(bonus)+'!')                    items_txt_rmv(item_function[0])                    print('')                    break	            if e_hp>0 and weapon.lower()!='back' and chosen_item.lower()!='back':                input()                print (e_name + ' attacks you!')                print('')                e_dmg = (e_atk - defense*(level/2))                battle_hp=battle_hp-e_dmg                print('You\'ve lost ' + str(int(e_dmg)) +' Hp!')                input()                if battle_hp>0:                    os.system('clear')                    print(e_name+'\'s health: ' + str(int(e_hp)))                    print('')                    print('')                    print('Your health: ' + str(int(battle_hp)))                    print('')                else:                    break        if _run:            print('You\'ve run away safely!')            input()            os.system('clear')        else:            if battle_hp>0:                print('The ' + e_name +' died!')                gained_xp= e_xp                print('You\'ve gained ' + str(int(gained_xp)) + ' xp!')                input()                os.system('clear')                return gained_xp            elif battle_hp<=0:                print('You died!')                input()                os.system('clear')